import React, { Component } from 'react';

import picture from "./picture.png";
import wallpaper from './wallpaper.png';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
  Card,
  Button,
  CardImg,
  CardText,
  CardBody,
  CardTitle,
  Container,
  Row,
  Navbar,
  Col
 }  from "reactstrap";


 export default class App extends React.Component {
  render() {
    const projetsData = [
      {name: 'FootSlap', desc: 'Jeu mobile ou tu choisis ton club de foot et tu notes les prestations de tes joueurs en leur mettant des "wows" ou des "slaps"', img: '/FTpicture.png'},
      {name: 'FootSlap', desc: 'Jeu mobile ou tu choisis ton club de foot et tu notes les prestations de tes joueurs en leur mettant des "wows" ou des "slaps"', img: '/FTpicture.png'},
      {name: 'FootSlap', desc: 'Jeu mobile ou tu choisis ton club de foot et tu notes les prestations de tes joueurs en leur mettant des "wows" ou des "slaps"', img: '/FTpicture.png'},

    ];
    var projetList = projetsData.map((projet, i) => {
      return(<Projet key={i} projetName={projet.name} projetDesc={projet.desc} projetImg={projet.img}/>)
    });



      return (
      <div  className = "BG" >
          <div className="App"   >
              <Navbar>

             <img src={picture}   roundedCircle   alt="persopicture"     style={{  margin : 0,left : 10,  position: 'absolute',
                                              marginRight : 100, width: 180, height: 180, borderRadius: 150}} 
                />
            <p className = 'title' style={{color: 'red', position : 'center' }}>
      VINCENT LAZARUS
           </p>
            <p className = 'title'>   
      Développeur Web
              </p>
            </Navbar>
               </div>

              <Container>
                         <Row >
                        {projetList}
                </Row>
                        </Container>

</div>
   );
      }
    }

          class Projet  extends Component {
            render () {
              var styleHeart = {
                color: '#fc6861',
                position: 'absolute',
                top: '5%',
                left: '80%',
                cursor: 'pointer'
              }


    return (

      <Col xs="12" sm="6" md="4" lg="3">
      <div style={{marginBottom:30, opacity : 0.5, width : "50%", height : "50%"}}>
        <Card>
        <CardImg top width="100%"  src={this.props.projetImg}  />
        <CardBody  style={{height: 250}}>
              <CardTitle>{this.props.projetName}</CardTitle>
              <CardText>{this.props.projetDesc}</CardText>
              <Button variant="primary">Lien Github</Button>

            </CardBody>
          </Card>
      </div>
    </Col>


    )
            }
          }
